/*
* Decode LED on Off
* 
*/
function decode_on_off(value){

	for(i=0;i<8;i++){
		leds[i]['status'] = (value & 1<<i)?'On':'Off';
		leds[i]['status_id'] = (value & 1<<i)?1:0;
	}			


}

/*
* Decode Blinking State
*/
function decode_blinking(value){

	for(i=0;i<8;i++){

		 (value & 1<<i) ? (leds[i]['status'] = 'Blinking'):'';
		 (value & 1<<i) ? (leds[i]['status_id'] = 2):'';		
	}			

}

