function raminfo(value){

	if(msg.value[0] == 0x90){
		msg.payload = "Command not supported:"+msg.value[1];
		return msg;
	}

	value = new Buffer(value,'hex');
	//Read Signed value
	signed = value.readInt16LE(1);
	//Read Unsigned Value
	unsinged = value.readUInt16LE(1);
	//Assign
	msg.values = {signed:signed,unsigned:unsinged};


	return msg;
}

function ram_interpret(value){
	if(value[0] == 0x8e) //Scale
	{
		//console.log(typeof value);
		scale = new Buffer(value,'hex');
		msg.signed = scale.readInt16LE(1);
		//Read Signed value
		scale = Math.abs(scale.readInt16LE(1))
		if (scale >= 0x4000){
			//v = 32768  value; 
			scale = 1/(32768 - scale);

		}else
		{
			scale = ""
		}
		context.ramscale[msg.value[0]] = scale;

		msg.scale =  scale; 
	}

	if(value[3] == 0x8F) //Offset
	{
		//console.log(typeof value);
		offset = new Buffer(value,'hex');
		//Read Signed value
		offset = Math.abs(offset.readInt16LE(4))	

		context.ramscale[msg.value[0]] = offset;
		msg.offset = offset; 
	}

	return {payload:msg}


}

